import React from "react";
import ChatBot from "react-simple-chatbot";
import moment from "moment";

function checkDateValidity(value) {
    const dateEntered = value;
    if (moment(dateEntered, "DD.MM.YYYY", true).isValid() && (moment(dateEntered, "DD.MM.YYYY", true) < moment.now())) {
        return true
    } else {
        return false
    }
}

function calculateAge(birthday) { // birthday is a date

    const today = new Date();
    const birthDate = new Date(birthday);
    let age = today.getFullYear() - birthDate.getFullYear();
    let m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
        age--;
        console.log(age);
    }
    





    //
    // const parts = birthday.match(/(\d+)/g);
    // // new Date(year, month [, date [, hours[, minutes[, seconds[, ms]]]]])
    // const birthdayTransformed = new Date(parts[2], parts[1] - 1, parts[0]); // months are 0-based
    // const ageDifMs = new Date() - birthdayTransformed.getTime();
    // console.log(new Date().getFullYear() - ageDifMs.getFullYear());

    if (age > 18 ) {
        return true;
    } else {
        return false
    }
}


class BnpChatbot extends React.Component {

    componentDidMount() {
        this.handleEnd = this.handleEnd.bind(this);
        const {steps} = this.props;
    }


    constructor(props) {
        super(props);


        this.state = {
            name: 'TOTOOOO',
            gender: '',
            age: '',
        };


    }

    handleEnd({steps, values}) {
        // console.log(steps);
        // console.log(values);
        console.log(`${values[0]}`)
    }


    render() {
        const {name, gender, age} = this.state;

        const config = {
            width: "300px",
            height: "400px",
            floating: true
        };


        const steps = [
            {
                id: "Greet",
                message: "Hello, Welcome to our BNP Chatbot",
                trigger: "Ask Gender"
            },
            {
                id: "Ask Gender",
                message: "Vous-êtes un homme ou une femme ?",
                trigger: "gender"
            },
            {
                id: "gender",
                options: [
                    {value: "Homme", label: "Homme", trigger: "Ask Name"},
                    {value: "Femme", label: "Femme", trigger: "Ask Name"}
                ]
            },
            {
                id: "Ask Name",
                message: "Merci, quel est votre prénom",
                trigger: "name"
            },
            {
                id: "name",
                user: true,
                trigger: "Personalize Greetings"
            },
            {
                id: "Personalize Greetings",
                message: "Enchanté {previousValue}, Joli prénom",
                trigger: "Asking for birthdate"
            },
            {
                id: "Asking for birthdate",
                message: "Quelle est votre date de naissance ? (format dd.mm.yyyy souhaité)",
                trigger: "Waiting for birthdate input"
            },

            {
                id: "Waiting for birthdate input",
                user: true,

                validator: (value) => {
                    if (calculateAge(value)) {
                        console.log('majeur');
                        return true;
                    }
                    console.log('mineur');
                    return 'mineur';
                },
                trigger: 'Done',
            },
            {
                id: 'Done',

                component: (
                    <button> This is the end</button>
                ),
                end: true,
            },

        ];
        return <div>
            <ChatBot steps={steps} {...config} handleEnd={this.handleEnd}/>
            <div>{this.state.name}</div>
        </div>


    }
}

export default BnpChatbot;
