import React from "react";
import ChatBot from "react-simple-chatbot";
function BnpChatbot(props) {

    const config = {
        width: "300px",
        height: "400px",
        floating: true
    };

    const steps = [
        {
            id: "Greet",
            message: "Hello, Welcome to our BNP Chatbot",
            trigger: "Ask Gender"
        },
        {
            id: "Ask Gender",
            message: "Vous-êtes un homme ou une femme ?",
            trigger: "Displaying gender options"
        },
        {
            id: "Displaying gender options",
            options: [
                {
                    value: "Homme",
                    label: "Homme",
                    trigger: "Done"
                },
                {
                    value: "Femme",
                    label: "Femme",
                    trigger: "Done"
                }
            ]
        },
        {
            id: "Ask Name",
            message: "Please type your name?",
            trigger: "Waiting user input for name"
        },
        {
            id: "Waiting user input for name",
            user: true,
            trigger: "Asking options to eat"
        },
        {
            id: "Asking options to eat",
            message: "Hi {previousValue}, Glad to know you !!",
            trigger: "Done"
        },
        {
            id: "Done",
            message: "Have a great day !!",
            end: true
        }
    ];
    return <ChatBot steps={steps} {...config} />;
}
export default BnpChatbot;
