import React from "react";
import ChatBot from "react-simple-chatbot";
import moment from "moment";

function checkDateValidity(value) {
    const dateEntered = value;
    if (moment(dateEntered, "DD.MM.YYYY", true).isValid() && dateEntered) {
        return true
    } else {
        return false
    }
}


function BnpChatbot(props) {

    const config = {
        width: "300px",
        height: "400px",
        floating: true
    };

    const steps = [
        {
            id: "Greet",
            message: "Hello, Welcome to our BNP Chatbot",
            trigger: "Ask Gender"
        },
        {
            id: "Ask Gender",
            message: "Vous-êtes un homme ou une femme ?",
            trigger: "Displaying gender options"
        },
        {
            id: "Displaying gender options",
            options: [
                {
                    value: "Homme",
                    label: "Homme",
                    trigger: "Ask Name"
                },
                {
                    value: "Femme",
                    label: "Femme",
                    trigger: "Ask Name"
                }
            ]
        },
        {
            id: "Ask Name",
            message: "Merci, quel est votre prénom",
            trigger: "Waiting user input for name"
        },
        {
            id: "Waiting user input for name",
            user: true,
            trigger: "Asking for name"
        },
        {
            id: "Asking for name",
            message: "Enchanté {previousValue}, Joli prénom",
            trigger: "Asking for birthdate"
        },
        {
            id: "Asking for birthdate",
            message: "Quelle est votre date de naissance ? (format dd.mm.yyyy souhaité)",
            trigger: "Waiting for birthdate input"
        },

        {
            id: "Waiting for birthdate input",
            user: true,

            validator: (value) => {
                if (checkDateValidity(value)) {
                    console.log('valid date')
                    return true;
                }
                console.log('not valid date');
                return 'Merci de fournir une date de naissance inférieure à la date du jour';
            },
            trigger: 'Done',
        },
        {
            id: "Done",
            message: "Have a great day !!",
            end: true
        }
    ];
    return <ChatBot steps={steps} {...config} />;
}

export default BnpChatbot;
