import React from "react";

function ButtonGroup(props) {
  const buttonWidth = {
    width: "600px"
  };
  return (
    <div>
      <div className="d-flex justify-content-center mt-3 mb-3">
        <button
          className="btn btn-danger btn-block"
          style={buttonWidth}
          onClick={() => {
            props.eventHandler("tomato");
          }}
        >
          Add/Remove Tomato
        </button>
      </div>

      <div className="d-flex justify-content-center mt-3 mb-3">

      </div>

      <div className="d-flex justify-content-center mt-3 mb-3">

      </div>

      <div className="d-flex justify-content-center mt-3 mb-3">

      </div>
    </div>
  );
}

export default ButtonGroup;
