import React from "react";
import ChatBot from "react-simple-chatbot";
function BnpChatbot(props) {

    const config = {
        width: "300px",
        height: "400px",
        floating: true
    };

    const steps = [
        {
            id: "Greet",
            message: "Bonjour, Êtes-vous un Homme ou une Femme ?",
            trigger: "Gender"
        },
        {
            id: "Gender",
            user: true,
        },
        {
            id: "Ask Name",
            message: "Merci, quel est votre prénom ?",
            trigger: "Waiting user input for name"
        },
        {
            id: "Waiting user input for name",
            user: true,
            trigger: "Asking what's the name user"
        },
        {
            id: "Done",
            message: "Bonne journée et à bientôt",
            end: true
        }
    ];
    return <ChatBot steps={steps} {...config} />;
}
export default BnpChatbot;
