import React from "react";
import ChatBot from "react-simple-chatbot";
import moment from "moment";
import { ThemeProvider } from 'styled-components';


function checkDateValidity(value) {
    const dateEntered = value;
    if (moment(dateEntered, "DD.MM.YYYY", true).isValid() && (moment(dateEntered, "DD.MM.YYYY", true) < moment.now())) {
        return true
    } else {
        return false
    }
}

function calculateAge(birthday) { // birthday is a date

    const today = new Date();
    const birthDate = new Date(birthday);
    let age = today.getFullYear() - birthDate.getFullYear();
    let m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
        age--;
        console.log(age);
       return age;
    }


    if (age >= 18) {
        return true;
    } else {
        return false
    }
}

function isItLegal(age) {
    this.state.age = age;

    if (age >= 18) {
        return <h1>Majeur</h1>;
    }
    return <h1>Mineur</h1>;
}



class BnpChatbot extends React.Component {

    // componentDidMount() {
    //     this.handleEnd = this.handleEnd.bind(this);
    //     const {steps} = this.props;
    // }


    constructor(props) {
        super(props);
        this.handleEnd = this.handleEnd.bind(this);


        this.state = {};


    }

    handleEnd({steps, values}) {

        this.state.gender = `${values[0]}`;
        this.state.name = `${values[1]}`;
        this.state.birth = `${values[2]}`;
        console.log(this.state.name);
        console.log(this.state.gender);
        console.log(this.state.birth);
        alert('Féliciations ' + this.state.name + ' vous avez ' + this.state.age);

    }


    render() {
        const {name, gender, birth, age} = this.state;

        const config = {
            width: "300px",
            height: "400px",
            floating: true
        };

        const image = ".../credito.png"

        const theme = {
            background: '#f5f8fb',
            fontFamily: 'Helvetica Neue',
            headerBgColor: '#b1daef',
            headerFontColor: '#fff',
            headerFontSize: '15px',
            botBubbleColor: '#efeedf',
            botFontColor: '#fff',
            userBubbleColor: '#52ff18',
            userFontColor: '#4a4a4a',
        };


        const steps = [
            {
                id: "Greet",
                message: "Hello, Welcome to our BNP Chatbot",
                trigger: "Ask Gender"
            },
            {
                id: "Ask Gender",
                message: "Vous-êtes un homme ou une femme ?",
                trigger: "gender"
            },
            {
                id: "gender",
                options: [
                    {value: "Homme", label: "Homme", trigger: "Ask Name"},
                    {value: "Femme", label: "Femme", trigger: "Ask Name"}
                ]
            },
            {
                id: "Ask Name",
                message: "Merci, quel est votre prénom",
                trigger: "name"
            },
            {
                id: "name",
                user: true,
                trigger: "Personalize Greetings"
            },
            {
                id: "Personalize Greetings",
                message: "Enchanté {previousValue}, Joli prénom",
                trigger: "Asking for birthdate"
            },
            {
                id: "Asking for birthdate",
                message: "Quelle est votre date de naissance ?",
                trigger: "Waiting for birthdate input"
            },

            {
                id: "Waiting for birthdate input",
                placeholder: "format dd.mm.yyyy souhaité",
                inputAttributes: this.state.name,
                user: true,

                validator: (value) => {

                    if (calculateAge(value)) {
                        console.log('majeur');
                        this.state.age =  calculateAge(value);
                        return true;
                    }
                    console.log('mineur');
                    this.state.age =  calculateAge(value);

                    return 'mineur';
                },

                trigger: 'Done',
            },

            {
                id: 'Done',
                component: (
                    <div>
                    <div> Merci d'avoir participé</div>
                    <button>RESTART</button>
                    </div>
                ),
                end: true,
            },

        ];
        return <ThemeProvider theme={theme}>
                <ChatBot steps={steps} {...config} handleEnd={this.handleEnd}/>
            </ThemeProvider>

    }
}

export default BnpChatbot;


