import React from "react";
import ChatBot from "react-simple-chatbot";
import moment from "moment";

function checkDateValidity(value) {
    const dateEntered = value;
    if (moment(dateEntered, "DD.MM.YYYY", true).isValid() && (moment(dateEntered, "DD.MM.YYYY", true) < moment.now())) {
        return true
    } else {
        return false
    }
}

class BnpChatbot extends React.Component {

    componentDidMount() {
        this.handleEnd = this.handleEnd.bind(this);
    }

    handleEnd({ steps, values }) {
        // console.log(steps);
        // console.log(values);
        alert(`Chat handleEnd callback! Number: ${values[0]}`);
    }

    constructor(props) {
        super(props);

        this.state = {
            name: '',
            gender: '',
            age: '',
        };
    }

    render() {
        const {name, gender, age} = this.state;

        const config = {
            width: "300px",
            height: "400px",
            floating: true
        };


        const steps = [
            {
                id: "Greet",
                message: "Hello, Welcome to our BNP Chatbot",
                trigger: "Ask Gender"
            },
            {
                id: "Ask Gender",
                message: "Vous-êtes un homme ou une femme ?",
                trigger: "gender"
            },
            {
                id: "gender",
                options: [
                    {value: "Homme", label: "Homme", trigger: "Ask Name"},
                    {value: "Femme", label: "Femme", trigger: "Ask Name"}
                ]
            },
            {
                id: "Ask Name",
                message: "Merci, quel est votre prénom",
                trigger: "name"
            },
            {
                id: "name",
                user: true,
                trigger: "Personalize Greetings"
            },
            {
                id: "Personalize Greetings",
                message: "Enchanté {previousValue}, Joli prénom",
                trigger: "Asking for birthdate"
            },
            {
                id: "Asking for birthdate",
                message: "Quelle est votre date de naissance ? (format dd.mm.yyyy souhaité)",
                trigger: "Waiting for birthdate input"
            },

            {
                id: "Waiting for birthdate input",
                user: true,

                validator: (value) => {
                    if (checkDateValidity(value)) {
                        console.log('valid date')
                        return true;
                    }
                    console.log('not valid date');
                    return 'Date invalide';
                },
                trigger: 'Done',
            },
            {
                id: 'Done',
                message: 'Thanks! {steps.name} Your data was submitted successfully!',
                end: true,
            },

        ];
        return <ChatBot steps={steps} {...config} handleEnd={this.handleEnd}
        />;
    }
}

export default BnpChatbot;
